import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { base } from "../../utils/airtableUtils";

export default function UserTeam() {
  //   const [team, setTeam] = useState([]);
  const [records, setRecords] = useState([]);
  const {
    query: { userId },
  } = useRouter();

  useEffect(() => {
    if (userId) {
      base("Draft Selections")
        .select({
          view: "Grid view",
        })
        .eachPage(
          function page(records) {
            records.forEach(function ({ fields }) {
              if (fields.userId === userId) {
                // console.log("fields.players", fields.players);
                setRecords(fields.players);
              }
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [userId]);

  useEffect(() => {
    let string = "({players} = 'rec2FXdmPdB7bZVxb')";
    if (records) {
      base("Draft Selections")
        .select({
          view: "Grid view",
          filterByFormula: string,
        })
        .eachPage(
          function page(records) {
            console.log("records", records);
            records.forEach(function (record) {
              console.log("record", record);
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [records]);
  return (
    <div>
      <h1>User Team</h1>
    </div>
  );
}
