import { useState, useEffect } from "react";
import { auth, initializeApp } from "firebase";

const defaultUser = {
  isLoggedIn: false,
  displayName: "",
};

console.log("firebasewindow.", window.firebase);
const firebaseConfig = {
  apiKey: "AIzaSyAxzP3rrdQYUxWO8JvAkuao8R2Q3lTu93Q",
  authDomain: "golf-draft-fdf5b.firebaseapp.com",
  databaseURL: "https://golf-draft-fdf5b.firebaseio.com",
  projectId: "golf-draft-fdf5b",
  storageBucket: "golf-draft-fdf5b.appspot.com",
  messagingSenderId: "696363008338",
  appId: "1:696363008338:web:38f1894846cdf9bd66ba8b",
  measurementId: "G-SLHJKPLPH6",
};
initializeApp(firebaseConfig);

export default function useAuth() {
  const [user, setUser] = useState(defaultUser);

  function signIn() {
    const provider = new auth.GoogleAuthProvider();
    auth()
      .signInWithPopup(provider)
      .then(res => {
        setUser({
          isLoggedIn: true,
          displayName: res.user.displayName,
          oAuthId: res.user.uid,
        });
      })
      .catch(err => {
        console.error(err);
        setUser({
          isLoggedIn: false,
          displayName: "",
        });
      });
  }

  function signOut() {
    auth()
      .signOut()
      .then(function () {
        console.log("sign out");
        setUser({
          isLoggedIn: false,
          displayName: "",
        });
      })
      .catch(function (error) {
        console.error(error);
      });
  }

  useEffect(() => {
    signIn();
  }, []);

  return {
    user,
    signIn,
    signOut,
  };
}
