import React from "react";
import styles from "./column.module.scss";

const Column = ({ data, heading, textAlign }) => {
  return (
    <>
      <h3 className={styles.columnTitle}>{heading}</h3>
      {data.length &&
        data.map(value => (
          <div className={styles.valueContainer} style={{ textAlign }}>
            <p>{value}</p>
          </div>
        ))}
    </>
  );
};

export default Column;
