import React, { useContext, useEffect } from "react";
import styles from "./home.module.scss";
import Auth from "../auth/auth";
import { AuthContext } from "../../context/authContext";
import { base } from "../../utils/airtableUtils";
import { useRouter } from "next/router";

export default function Home() {
  const {
    user: { isLoggedIn, oAuthId },
  } = useContext(AuthContext);

  const Router = useRouter();

  useEffect(() => {
    if (isLoggedIn) {
      base("Draft Selections")
        .select({
          view: "Grid view",
        })
        .eachPage(
          function page(records) {
            if (records.length === 0) {
              Router.push("/draftBoard");
            }
            records.forEach(function ({ fields }) {
              if (fields.userId === oAuthId) {
                console.log("User has team");
                Router.push("/team");
                return;
              } else {
                console.log("User has no team");
                Router.push("/draftBoard");
              }
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [isLoggedIn]);

  return (
    <div className={styles.app}>
      <Auth />
      <h1>Welcome to the Golf Pool</h1>
    </div>
  );
}
