import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { getAirtableData } from "../../utils/airtableUtils";
import Auth from "../auth/auth";
import Player from "../../components/shared/player/player";
import styles from "./userTeam.module.scss";

export default function UserTeam() {
  const [teamData, setTeamData] = useState({});
  const {
    query: { userId },
  } = useRouter();

  function formatTeam(fields) {
    let team = [];
    for (let i = 0; i < fields.players.length; i++) {
      const player = {
        name: fields["name (from Players)"][i],
        flag: fields["flag (from Players)"][i],
        ranking: fields["flag (from Players)"][i],
      };
      team.push(player);
    }
    return team;
  }

  useEffect(() => {
    if (userId) {
      getAirtableData("Draft Selections", callback);
    }

    function callback(records) {
      records.forEach(function ({ fields }) {
        if (fields.userId === userId) {
          // setTeamData(formatTeam(fields));
          console.log("fields", fields);
          setTeamData(fields);
        }
      });
    }
  }, [userId]);

  console.log("teamData", typeof teamData);

  return (
    <div className={styles.teamPage}>
      <Auth />
      <div className={styles.playerChart}>
        {teamData.length && (
          <div className={styles.nameColumn}>
            <h3>Players</h3>
            {teamData["name (from Players)"].map(name => {
              console.log("name", name);
              return (
                <div className={styles.playerContainer}>
                  <p>{name}</p>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}
