import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { base } from "../../utils/airtableUtils";

export default function UserTeam() {
  const [teamData, setTeamData] = useState([]);
  const {
    query: { userId },
  } = useRouter();

  useEffect(() => {
    if (userId) {
      base("Draft Selections")
        .select({
          view: "Grid view",
        })
        .eachPage(
          function page(records) {
            records.forEach(function ({ fields }) {
              if (fields.userId === userId) {
                // setTeamData(fields);
                let team = [];
                console.log("fields", fields);
                for (let i = 0; i < fields.players.length; i++) {
                  const player = {
                    name: fields.name[i],
                    flag: fields.flag[i],
                    ranking: fields.flag[i],
                  };
                  team.push(player);
                }
                setTeamData(team);
              }
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [userId]);

  console.log("teamData", teamData);

  return (
    <div>
      <h1>User Team</h1>
    </div>
  );
}
