import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { base } from "../../utils/airtableUtils";

export default function UserTeam() {
  const [teamData, setTeamData] = useState([]);
  const {
    query: { userId },
  } = useRouter();

  useEffect(() => {
    if (userId) {
      base("Draft Selections")
        .select({
          view: "Grid view",
        })
        .eachPage(
          function page(records) {
            records.forEach(function ({ fields }) {
              if (fields.userId === userId) {
                setTeamData(fields);
              }
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [userId]);

  //   console.log("teamData", teamData);
  if (teamData.length > 0) {
    let team = [];
    for (let i = 0; i < teamData.players.length; i++) {
      const player = {
        name: teamData.name[i],
        flag: teamData.flag[i],
        ranking: teamData.flag[i],
      };
      console.log("player", player);
      team.push(player);
    }
    console.log("team", team);
  }

  return (
    <div>
      <h1>User Team</h1>
    </div>
  );
}
