import React, { useContext, useEffect } from "react";
import Draft from "../../pages/draft/draft";
import styles from "./layout.module.scss";
import Auth from "../auth/auth";
import { AuthContext } from "../../context/authContext";
import { base } from "../../utils/airtableUtils";

export default function Layout() {
  const {
    user: { isLoggedIn, oAuthId },
  } = useContext(AuthContext);

  useEffect(() => {
    if (isLoggedIn) {
      base("Draft Selections")
        .select({
          view: "Grid view",
        })
        .eachPage(
          function page(records) {
            // This function (`page`) will get called for each page of records.

            records.forEach(function ({ fields }) {
              if (fields.userId === oAuthId) {
                console.log("fields", fields);
                return;
              }
            });
          },
          function done(err) {
            if (err) {
              console.error(err);
              return;
            }
          }
        );
    }
  }, [isLoggedIn]);

  return (
    <div className={!isLoggedIn ? styles.welcome : ""}>
      <Auth />
      {isLoggedIn ? <Draft /> : <h1>Welcome to the Golf Pool</h1>}
    </div>
  );
}
