import React, { useContext, useEffect } from "react";
import styles from "./home.module.scss";
import Auth from "../auth/auth";
import { AuthContext } from "../../context/authContext";
import { getAirtableData } from "../../utils/airtableUtils";
import { useRouter } from "next/router";

function callback(records) {
  if (records.length === 0) {
    Router.push("/draftBoard");
  }
  records.forEach(function ({ fields }) {
    if (fields.userId === oAuthId) {
      console.log("User has team");
      Router.push({
        pathname: "/team",
        query: { userId: oAuthId },
      });
    }
  });
}

export default function Home() {
  const {
    user: { isLoggedIn, oAuthId },
  } = useContext(AuthContext);

  const Router = useRouter();

  useEffect(() => {
    if (isLoggedIn) {
      getAirtableData("Draft Selections", callback);
    }
  }, [isLoggedIn]);

  return (
    <div className={styles.app}>
      <Auth />
      <h1>Welcome to the Golf Pool</h1>
    </div>
  );
}
