import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import { base, getAirtableData } from "../../utils/airtableUtils";

export default function UserTeam() {
  const [teamData, setTeamData] = useState([]);
  const {
    query: { userId },
  } = useRouter();

  function formatTeam(fields) {
    let team = [];
    for (let i = 0; i < fields.players.length; i++) {
      const player = {
        name: fields["name (from Players)"][i],
        flag: fields["flag (from Players)"][i],
        ranking: fields["flag (from Players)"][i],
      };
      team.push(player);
    }
    return team;
  }

  useEffect(() => {
    if (userId) {
      //   base("Draft Selections")
      //     .select({
      //       view: "Grid view",
      //     })
      //     .eachPage(
      //       function page(records) {
      //         records.forEach(function ({ fields }) {
      //           if (fields.userId === userId) {
      //             setTeamData(formatTeam(fields));
      //           }
      //         });
      //       },
      //       function done(err) {
      //         if (err) {
      //           console.error(err);
      //           return;
      //         }
      //       }
      //     );
      function callback() {
        if (fields.userId === userId) {
          setTeamData(formatTeam(fields));
        }
      }
      getAirtableData("Draft Selections");
    }
  }, [userId]);

  return (
    <div>
      <h1>User Team</h1>
      {teamData &&
        teamData.map(({ name }) => (
          <div>
            <p>{name}</p>
          </div>
        ))}
    </div>
  );
}
